
def add_two_numbers(x, y):     
    """calculates the sum of x and y.     
    Parameters:     
       x (int): a number
       y (int): another number    
    Returns:     
       int: sum of x and y
    """      
    #return _private__function_add(x, y)
    return x+y


def subtract_two_numbers(x,y):
    """calculates the subtraction of x and y.     
    Parameters:     
       x (int): a number
       y (int): another number    
    Returns:     
       int: Subtracted value of x and y
    """  
    #return _private__function_sub(x, y)
    return (x-y)


def multiply_two_numbers(x,y):
    """calculates the multiplication of x and y.     
    Parameters:     
       x (int): a number
       y (int): another number    
    Returns:     
       int: multiplied x and y
    """  
   # return _private__function_multiply(x, y)
    return x*y

def divide_two_numbers(x,y):
    """calculates the division of x and y.     
    Parameters:     
       x (int): a number
       y (int): another number    
    Returns:     
       int: Quotient of x and y
    """  
   # return _private__function_divivde(x, y)
    return (x/y)